#!/bin/sh #Get your favourite book from DLI: Specify the start page and end page, and this script takes care of the rest. #Caveat: you'll need to specify the base URL, though. PATH=/bin:/usr/bin:/usr/local/bin progname=`basename $0` case $# in 0) 1>&2 echo $progname: usage $progname start end; exit 1 ;; esac start=$1 end=$2 prefix=0000 echo "Enter path for the file to read:" read file #exec > $HOME/log_dli.txt x=1 lns=`wc -l $file` echo "LNS: $lns" index=`expr $start` while [ $x -lt $(wc -l &1 wget --timestamping --progress=dot:mega \ "$url$prefix$digits$index.tif") # wget $url$prefix$digits$index.tif if [ $? -ne 0 ] then # wget had problems. echo 1>&2 $0: "$WGET_OUTPUT" Exiting. fi if (echo "$WGET_OUTPUT" | fgrep 'Not Found') > /dev/null then break else echo "~~~~ Page found. Downloaded. ~~~~ \n" fi index=`expr $index + 1` done cd .. x=`expr $x + 1` done 
